import styled from 'styled-components'

const Form = styled.form`
	padding: 3rem;
	border-radius: 0 0 1rem 1rem;
	width: 100%;
	border: 1px solid black;

	@media only screen and (max-width: 768px) {
		display: flex;
		flex-direction: column;
		align-items: center;
		/* padding: 1rem 1rem 3rem; */
	}
`

export default Form
