import React from 'react'
import styled, {css} from 'styled-components';
import { Stepper } from 'react-form-stepper';

import useI18n from 'core/i18n/hooks/useI18n'

import {FORM_TITLE} from './locales'
import Text from 'common/components/Text'

const SubmitButton = styled.button`
    font: inherit;
    border-radius: 3px;
    padding: 0.5rem 1rem;
    width: 10rem;
    background: transparent;
    border: 2px solid black;
    cursor: pointer;

    &:hover {
        background: black;
        color: white;
    }
`;

const Form = styled.form`
    border: 1px solid black;
    padding: 1rem 2rem;
    width: 60vw;
    border-radius: 5%;
`;

const FormControl = styled.div`
    display: flex;
    flex-direction: column;
    margin: 1rem 0;
`;

// const Stepper = styled.div`
//     display: flex;
//     width: 100%;
//     border: 2px solid black;
//     justify-content: space-between;
//     margin: 2rem 0;
// `;

// const Step = styled.div`
//     width: 100%;
//     border-radius: 0 10rem 10rem 0;
//     text-align: center;
// `;

const RegisterForm = () => {
    const I18n = useI18n()

    return (
        <div>
            <Text as={"h1"} textAlign={"center"}>{I18n.t(FORM_TITLE)}</Text>
            <Stepper steps={[{label: 'Step 1'}, {label: 'Step 2'}, {label: 'Step 3'}, {label: 'Step 4'}]} activeStep={0}/>
            <Form>
                <FormControl>
                    <label>First Name</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Last Name</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Nickname</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Allergic food</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Congenital Disease</label>
                    <input type="text"/>
                </FormControl>
                <SubmitButton>Register</SubmitButton>
            </Form>
        </div>
    )
}

export default RegisterForm
