import styled, {css} from "styled-components";

const ErrorMessage = styled.span`
    position: absolute;
    left: 0;
    bottom: ${props => props.show ? '-1.5rem' : '0rem'};
    color: red;
    transition: bottom 0.1s ease;
`;

export default ErrorMessage;
