import React, {useState} from 'react'
import styled from 'styled-components';
import {useForm} from "react-hook-form";
import * as yup from 'yup';
import {yupResolver} from '@hookform/resolvers/yup'

import useI18n from 'core/i18n/hooks/useI18n'

import {FORM_TITLE} from './locales'
import Text from 'common/components/Text'
import ContentContainer from 'common/components/ContentContainer';
import UserInfo from "../UserInfo";
import EventInfo from "../EventInfo";
import Covid19 from "../Covid19";
import Confirmation from "../Confirmation";

interface UserInfoData {
    firstName: string
    lastName: string
    nickname: string
    conDisease: string
    allergicFood: string
    email: string,
    phoneNumber: string
}

// type Covid19Data = {
//
// }
//

interface EventInfoData {
    interestedTopic: string
    shirt: boolean
    shirtSize: string
}

const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/

const userInfoSchema = yup.object().shape({
    firstName: yup.string().required('First name is required'),
    lastName: yup.string().required('Last name is required'),
    nickname: yup.string().required('Nickname is required'),
    email: yup.string().email('Invalid email address').required('Email address is required'),
    phoneNumber: yup.string().matches(phoneRegExp, 'Invalid phone number').required('Phone number is required'),
    conDisease: yup.string(),
    allergicFood: yup.string(),
})

const covid19Schema = yup.object().shape({})

const eventInfoSchema = yup.object().shape({
    interestedTopic: yup.string().required('Interested topic is required'),
    shirt: yup.boolean(),
    shirtSize: yup.string().required('Shirt size is required')
})

const Button = styled.button`
    font: inherit;
    border-radius: 3px;
    padding: 0.5rem 1rem;
    width: 10rem;
    background: transparent;
    border: 2px solid black;
    cursor: pointer;


    &:hover {
        background: black;
        color: white;
    }

    @media only screen and (max-width: 768px) {
        width: 6rem;
        text-align: center;
    }
`;

const NextButton = styled(Button)`
    background: pink;
    float: right;

    &:hover {
        background: hotpink;
    }
`;

const PreviousButton = styled(Button)`
    visibility: ${props => props.visibility};
`;

const SubmitButton = styled(Button)`
    float: right;
`;

const Form = styled.form`
    width: 60vw;
    border: 1px solid black;
    padding: 1rem 2rem;
    border-radius: 1rem;

    @media only screen and (max-width: 768px) {
        display: flex;
        flex-direction: column;
        align-items: center;
    }
`;

const ButtonGroup = styled.div`
    padding: 1rem 2rem;

    @media only screen and (max-width: 768px) {
        display: flex;
        justify-content: space-between;
        padding: 1rem 1rem;
    }
`;

const Stepper = styled.div`
    display: flex;
    height: 3rem;
    border: 1px solid black;
    margin-top: 2rem;
    justify-content: space-around;
    align-items: center;
    border-radius: 1rem 1rem;

    @media only screen and (max-width: 768px) {
        font-size: 0.8rem;
        height: 2rem;
    }
`;

const Step = styled.div`
`;

const getSteps = () => {
    return ['User Info', 'Covid-19', 'Event Info', 'Confirmation']
};

const RegisterForm = () => {
    const I18n = useI18n()

    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();

    const userInfoForm = useForm<UserInfoData>({
        resolver: yupResolver(userInfoSchema)
    })
    const covid19Form = useForm({
        resolver: yupResolver(covid19Schema)
    })
    const eventInfoForm = useForm({
        resolver: yupResolver(eventInfoSchema)
    })

    const getStepContent = (stepIndex: number) => {
        switch (stepIndex) {
            case 0:
                return <UserInfo formProps={userInfoForm}/>;
            case 1:
                return <Covid19 formProps={covid19Form}/>;
            case 2:
                return <EventInfo formProps={eventInfoForm}/>;
            case 3:
                return <Confirmation/>;
            default:
                return 'Unknown Step Content';
        }
    };

    const handlePrevious = () => {
        setActiveStep(prevState => prevState - 1);
    }

    const handleNext = () => {
        setActiveStep(prevState => prevState + 1);
    };

    const onSubmit = (data: any) => {
        console.log(data)
        handleNext()
    }

    let submitState = null
    if (activeStep === 0) {
        submitState = userInfoForm.handleSubmit(onSubmit)
    } else if (activeStep === 1) {
        submitState = covid19Form.handleSubmit(onSubmit)
    } else if (activeStep === 2) {
        submitState = eventInfoForm.handleSubmit(onSubmit)
    }

    return (
        <ContentContainer>
            <Text as={"h1"} textAlign={"center"}>{I18n.t(FORM_TITLE)}</Text>
            <Stepper>
                {steps.map((stepTitle) => {
                    return <Step key={stepTitle}>{stepTitle}</Step>
                })}
            </Stepper>
            <Form>
                {getStepContent(activeStep)}
            </Form>
            <ButtonGroup>
                <PreviousButton
                    onClick={handlePrevious}
                    visibility={activeStep === 0 ? 'hidden' : 'visible'}
                >Back</PreviousButton>
                {activeStep !== steps.length - 1 &&
                <NextButton
                    onClick={submitState}
                    hidden={activeStep === steps.length - 1}
                >Next</NextButton>
                }
                {activeStep === steps.length - 1 &&
                <SubmitButton
                >Register</SubmitButton>
                }
            </ButtonGroup>
        </ContentContainer>
    )
}

export default RegisterForm
