import styled from "styled-components";

const Form = styled.form`
    width: 60vw;
    border: 1px solid black;
    padding: 0 2rem 2rem;
    border-radius: 0 0 1rem 1rem;

    @media only screen and (max-width: 768px) {
        display: flex;
        flex-direction: column;
        align-items: center;
    }
`;

export default Form
