import styled from "styled-components";
import Label from 'common/components/Label'
import Span from 'common/components/Span'

const TextInput = styled.input.attrs({
    type: 'text'
})`
    flex: 1;
    margin-top: 0.5rem;
    padding: 0 0.75rem;
    height: 2rem;
    font: inherit;
    position: relative;
    font-size: 1rem;
    border: none;
    //border-bottom: ${props => props.error ? '1px solid red' : '1px solid black'};
    outline: none;

    &:focus + ${Label}:after, &:not(:placeholder-shown) + ${Label}:after {
        transform: translateX(0%);
        opacity: 100%;
    }

    &:focus + ${Label} ${Span}, &:not(:placeholder-shown) + ${Label} ${Span} {
        transform: translateY(-120%);
        font-size: 0.9rem;
        color: coral;
    }
`;

export default TextInput;
