import React, {useState} from 'react'
import styled from 'styled-components';
import {useForm} from "react-hook-form";
import * as yup from 'yup';
import {yupResolver} from '@hookform/resolvers/yup'

import useI18n from 'core/i18n/hooks/useI18n'

import {FORM_TITLE} from './locales'
import Text from 'common/components/Text'
import ContentContainer from 'common/components/ContentContainer';
import UserInfo from "../UserInfo";
import EventInfo from "../EventInfo";
import Covid19 from "../Covid19";
import Confirmation from "../Confirmation";
import Col from "../../../../common/components/Col";
import FormControl from "../../../../common/components/FormControl";
import TextInput from "../../../../common/components/TextInput";
import Row from "../../../../common/components/Row";

interface UserInfoData {
    firstName: string
    lastName: string
    nickname: string
    conDisease: string
    allergicFood: string
}

// type Covid19Data = {
//
// }
//
// type EventInfoData = {
//
// }

const userInfoSchema = yup.object().shape({
    firstName: yup.string().required('This field is required'),
    lastName: yup.string().required('This field is required'),
    nickname: yup.string().required('This field is required'),
    conDisease: yup.string().required('This field is required'),
    allergicFood: yup.string().required('This field is required')
})

const covid19Schema = yup.object().shape({})

const eventInfoSchema = yup.object().shape({})

const Button = styled.button`
    font: inherit;
    border-radius: 3px;
    padding: 0.5rem 1rem;
    width: 10rem;
    background: transparent;
    border: 2px solid black;
    cursor: pointer;


    &:hover {
        background: black;
        color: white;
    }

    @media only screen and (max-width: 768px) {
        width: 6rem;
        text-align: center;
    }
`;

const NextButton = styled(Button)`
    background: pink;
    float: right;

    &:hover {
        background: hotpink;
    }
`;

const PreviousButton = styled(Button)`
    visibility: ${props => props.visibility};
`;

const SubmitButton = styled(Button)`
    float: right;
`;

const Form = styled.form`
    width: 60vw;
    border: 1px solid black;
    padding: 1rem 2rem;
    border-radius: 1rem;

    @media only screen and (max-width: 768px) {
        display: flex;
        flex-direction: column;
        align-items: center;
    }
`;

const ButtonGroup = styled.div`
    padding: 1rem 2rem;

    @media only screen and (max-width: 768px) {
        display: flex;
        justify-content: space-between;
        padding: 1rem 1rem;
    }
`;

const Stepper = styled.div`
    display: flex;
    height: 3rem;
    border: 1px solid black;
    margin-top: 2rem;
    justify-content: space-around;
    align-items: center;
    border-radius: 1rem 1rem;

    @media only screen and (max-width: 768px) {
        font-size: 0.8rem;
        height: 2rem;
    }
`;

const Step = styled.div`
`;

const getSteps = () => {
    return ['User Info', 'Covid-19', 'Event Info', 'Confirmation']
};

const RegisterForm = () => {
    const I18n = useI18n()

    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();

    const { register, errors, handleSubmit } = useForm<UserInfoData>()

    const userInfoForm = useForm<UserInfoData>({
        resolver: yupResolver(userInfoSchema)
    })
    const covid19Form = useForm({
        resolver: yupResolver(covid19Schema)
    })
    const eventInfoForm = useForm({
        resolver: yupResolver(eventInfoSchema)
    })

    const getStepContent = (stepIndex: number) => {
        switch (stepIndex) {
            case 0:
                return <UserInfo formProps={userInfoForm}/>;
            case 1:
                return <Covid19 formProps={covid19Form}/>;
            case 2:
                return <EventInfo formProps={eventInfoForm}/>;
            case 3:
                return <Confirmation/>;
            default:
                return 'Unknown Step Content';
        }
    };

    const handleNext = () => {
        setActiveStep(prevState => prevState + 1);
    };

    const onSubmit = (data: any) => {
        console.log(data)
        handleNext()
    }

    // const formSubmit = () => {
    //     if (activeStep === 0) {
    //         return userInfoForm.handleSubmit(onSubmit)
    //     } else if (activeStep === 1) {
    //         return covid19Form.handleSubmit(onSubmit)
    //     } else if (activeStep === 2) {
    //         return eventInfoForm.handleSubmit(onSubmit)
    //     }
    // }

    return (
        <ContentContainer>
            <Text as={"h1"} textAlign={"center"}>{I18n.t(FORM_TITLE)}</Text>
            <Stepper>
                {steps.map((stepTitle) => {
                    return <Step key={stepTitle}>{stepTitle}</Step>
                })}
            </Stepper>
            <Form onSubmit={handleSubmit((data: UserInfoData) => console.log(data))}>
                {/*{getStepContent(activeStep)}*/}
                <Row>
                    <Col>
                        <FormControl>
                            <label>First Name</label>
                            <TextInput name="firstName" ref={register}/>
                            {errors.firstName && errors.firstName.message}
                        </FormControl>
                        <FormControl>
                            <label>Last Name</label>
                            <TextInput name="lastName" ref={register}/>
                            {errors.lastName && errors.lastName.message}
                        </FormControl>
                        <FormControl>
                            <label>Nickname</label>
                            <TextInput name="nickname" ref={register}/>
                            {errors.nickname && errors.nickname.message}
                        </FormControl>
                    </Col>
                    <Col>
                        <FormControl>
                            <label>Congenital Disease</label>
                            <TextInput name="conDisease" ref={register}/>
                            {errors.conDisease && errors.conDisease.message}
                        </FormControl>
                        <FormControl>
                            <label>Allergic food</label>
                            <TextInput name="allergicFood" ref={register}/>
                            {errors.allergicFood && errors.allergicFood.message}
                        </FormControl>
                    </Col>
                </Row>
                {/*<Button type="submit">Submit</Button>*/}
                <ButtonGroup>
                    <PreviousButton
                        type={'button'} onClick={() => setActiveStep(activeStep - 1)}
                        visibility={activeStep === 0 ? 'hidden' : 'visible'}
                    >Back</PreviousButton>
                    {activeStep !== steps.length - 1 &&
                    <NextButton
                        hidden={activeStep === steps.length - 1}
                    >Next</NextButton>
                    }
                    {activeStep === steps.length - 1 &&
                    <SubmitButton
                        type="submit"
                    >Register</SubmitButton>
                    }
                </ButtonGroup>
            </Form>
        </ContentContainer>
    )
}

export default RegisterForm
