import React, {useState} from 'react'
import styled, {css} from 'styled-components';

import useI18n from 'core/i18n/hooks/useI18n'

import {FORM_TITLE} from './locales'
import Text from 'common/components/Text'

const SubmitButton = styled.button`
    font: inherit;
    border-radius: 3px;
    padding: 0.5rem 1rem;
    width: 10rem;
    background: transparent;
    border: 2px solid black;
    cursor: pointer;

    &:hover {
        background: black;
        color: white;
    }
`;

const NextButton = styled(SubmitButton)`
    background: aquamarine;
    display: block;
    margin-left: auto;
`;

const PreviousButton = styled(SubmitButton)`
    background: cadetblue;
    display: inline;
`;

const Form = styled.form`
    border: 1px solid black;
    padding: 1rem 2rem;
    width: 60vw;
    border-radius: 5%;
`;

const FormControl = styled.div`
    display: flex;
    flex-direction: column;
    margin: 1rem 0;
`;

const Stepper = styled.div`
    display: flex;
    width: 100%;
    border: 2px solid black;
    margin: 2rem 0;
`;

const Step = styled.div`
    width: 100%;
    border-radius: 0 10rem 10rem 0;
    text-align: center;
`;

const getSteps = () => {
    return ['1. User Info', '2. Covid-19', '3. Event Info', '4. Confirmation']
};

const RegisterForm = () => {
    const I18n = useI18n()

    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();

    const getStepContent = (stepIndex) => {
        switch (stepIndex) {
            case 0:
                return <UserInfo/>;
            case 1:
                return <Covid19/>;
            case 2:
                return <EventInfo/>;
            case 3:
                return <Confirmation/>;
        }
    };

    return (
        <div>
            <Text as={"h1"} textAlign={"center"}>{I18n.t(FORM_TITLE)}</Text>
            <Stepper>
                <Step>1. User Info</Step>
                <Step>2. Covid-19</Step>
                <Step>3. Event Info</Step>
                <Step>4. Confirmation</Step>
            </Stepper>
            <Form>
                {getStepContent(activeStep)}
                <PreviousButton type={'button'} onClick={() => setActiveStep(activeStep - 1)}>Back</PreviousButton>
                {activeStep !== getSteps().length - 1 &&
                    <NextButton type={'button'} onClick={() => setActiveStep(activeStep + 1)}>Next</NextButton>
                }
                {activeStep === getSteps().length - 1 &&
                    <SubmitButton type={'button'}>Register</SubmitButton>
                }
            </Form>
        </div>
    )
}

const UserInfo = () => {
    return (
        <div>
            <FormControl>
                <label>First Name</label>
                <input type="text"/>
            </FormControl>
            <FormControl>
                <label>Last Name</label>
                <input type="text"/>
            </FormControl>
            <FormControl>
                <label>Nickname</label>
                <input type="text"/>
            </FormControl>
            <FormControl>
                <label>Allergic food</label>
                <input type="text"/>
            </FormControl>
            <FormControl>
                <label>Congenital Disease</label>
                <input type="text"/>
            </FormControl>
        </div>
    )
}

const Covid19 = () => {
    return (
        <Text>Covid-19</Text>
    )
}

const EventInfo = () => {
    return (
        <Text>Event Info</Text>
    )
}

const Confirmation = () => {
    return (
        <Text>Confirmation</Text>
    )
}

export default RegisterForm
