import styled, {css} from "styled-components";

const ErrorMessage = styled.span`
    position: absolute;
    left: 0;
    bottom: 0;
    color: red;
    transition: bottom 0.5s ease-in;
    visibility: hidden;

    ${props => props.show && css`
        visibility: visible
        bottom: -1.5rem
    `}
`;

export default ErrorMessage;
