import styled from "styled-components";

const TextInput = styled.input.attrs({
    type: 'text'
})`
    flex: 1;
    margin-top: 0.5rem;
    padding: 0 0.75rem;
    height: 2rem;
    font: inherit;
    font-size: 1rem;
    border: none;
    border-bottom: ${props => props.error ? '1px solid red' : '1px solid black'};
    outline: none;

`;

export default TextInput;
