import styled from 'styled-components'

export const Radio = styled.input.attrs({
	type: 'radio',
})`
	position: absolute;
	opacity: 0;
	cursor: pointer;
`

export const RadioLabel = styled.label`
	display: block;
	position: relative;
	padding-left: 35px;
	margin-bottom: 12px;
	cursor: pointer;
	font-size: 22px;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
`

export const Checkmark = styled.span`
	position: absolute;
	top: 0;
	left: 0;
	height: 25px;
	width: 25px;
	background-color: #eee;
	border-radius: 50%;
`
