import styled, {css} from "styled-components";

const ErrorMessage = styled.span`
    position: absolute;
    left: 0;
    font-size: ${props => props.show ? '1rem' : '0.5rem'};
    bottom: ${props => props.show ? '-1.5rem' : '0rem'};
    color: red;
    transition: bottom 0.2s ease-out, font-size 0.2s ease-out;
`;

export default ErrorMessage;
