import React, {useState} from 'react'
import styled, {css} from 'styled-components';

import useI18n from 'core/i18n/hooks/useI18n'

import {FORM_TITLE} from './locales'
import Text from 'common/components/Text'
import Flex from 'common/components/Flex'

const Button = styled.button`
    font: inherit;
    border-radius: 3px;
    padding: 0.5rem 1rem;
    width: 10rem;
    background: transparent;
    border: 2px solid black;
    cursor: pointer;

    &:hover {
        background: black;
        color: white;
    }
`;

const NextButton = styled(Button)`
    background: pink;
    float: right;

    &:hover {
        background: hotpink;
    }
`;

const PreviousButton = styled(Button)`
    visibility: ${props => props.visibility};
`;

const Form = styled.form`
    border: 1px solid black;
    padding: 1rem 2rem;
    width: 60vw;
    border-radius: 5%;
`;

const FormControl = styled.div`
    display: flex;
    flex-direction: column;
    margin: 1.5rem;
`;

const ButtonGroup = styled.div`
    width: 100%;
`;

const Stepper = styled.div`
    display: flex;
    width: 100%;
    border: 2px solid black;
    margin: 2rem 0;
`;

const Step = styled.div`
    width: 100%;
    border-radius: 0 10rem 10rem 0;
    text-align: center;
`;

const Grid = styled.div`
    display: grid;
    grid-template-areas: "col1 col2";
`;

const Column1 = styled.div`
    grid-area: col1;
`;

const Column2 = styled.div`
    grid-area: col2;
`;

const TextInput = styled.input.attrs({
    type: 'text'
})`
    height: 2rem;
    font-size: 1rem;
    border: 1px solid black;
    border-radius: 3px;
`;

const getSteps = () => {
    return ['1. User Info', '2. Covid-19', '3. Event Info', '4. Confirmation']
};

const RegisterForm = () => {
    const I18n = useI18n()

    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();

    const getStepContent = (stepIndex) => {
        switch (stepIndex) {
            case 0:
                return <UserInfo/>;
            case 1:
                return <Covid19/>;
            case 2:
                return <EventInfo/>;
            case 3:
                return <Confirmation/>;
        }
    };

    return (
        <div>
            <Text as={"h1"} textAlign={"center"}>{I18n.t(FORM_TITLE)}</Text>
            <Stepper>
                {steps.map((stepTitle) => {
                    return <Step>{stepTitle}</Step>
                })}
            </Stepper>
            <Form>
                {getStepContent(activeStep)}
                <ButtonGroup>
                    <PreviousButton
                        type={'button'} onClick={() => setActiveStep(activeStep - 1)}
                        visibility={activeStep === 0 ? 'hidden' : 'visible'}
                    >Back</PreviousButton>
                    {activeStep !== steps.length - 1 &&
                    <NextButton
                        type={'button'} onClick={() => setActiveStep(activeStep + 1)}
                    >Next</NextButton>
                    }
                    {activeStep === steps.length - 1 &&
                    <Button
                        type={'button'}
                    >Register</Button>
                    }
                </ButtonGroup>
            </Form>
        </div>
    )
}

const UserInfo = () => {
    return (
        <Grid>
            <Column1>
                <FormControl>
                    <label>First Name</label>
                    <TextInput/>
                </FormControl>
                <FormControl>
                    <label>Nickname</label>
                    <TextInput/>
                </FormControl>
                <FormControl>
                    <label>Congenital Disease</label>
                    <TextInput/>
                </FormControl>
            </Column1>
            <Column2>
                <FormControl>
                    <label>Last Name</label>
                    <TextInput/>
                </FormControl>
                <FormControl>
                    <label>Allergic food</label>
                    <TextInput/>
                </FormControl>
            </Column2>
        </Grid>
    )
}

const Covid19 = () => {
    return (
        <Text>Covid-19</Text>
    )
}

const EventInfo = () => {
    return (
        <Text>Event Info</Text>
    )
}

const Confirmation = () => {
    return (
        <Text>Confirmation</Text>
    )
}

export default RegisterForm
