import styled from "styled-components";

const Stepper = styled.div`
    display: flex;
    height: 3rem;
    border-bottom: none;
    margin-top: 2rem;
    align-items: center;
    position: relative;
    overflow: hidden;

    @media only screen and (max-width: 768px) {
        font-size: 0.5rem;
        height: 2rem;
    }
`;

export default Stepper;
