import styled from "styled-components";

const Stepper = styled.div`
    width: 100%;
    display: flex;
    height: 3rem;
    border: 1px solid black;
    margin-top: 2rem;
    align-items: center;
    border-radius: 1rem 1rem;
    position: relative;

    @media only screen and (max-width: 768px) {
        font-size: 0.8rem;
        height: 2rem;
    }
`;

export default Stepper;
