import styled from "styled-components";

const Label = styled.label`
    position: absolute;
    left: 0;
    bottom: 0;
    width: 100%;
    pointer-events: none;

    &:after {
        position: absolute;
        content: "";
        left: 0;
        bottom: -0.40rem;
        border-bottom: 2px solid coral;
        width: 100%;
        height: 100%;
        transform: translateX(-110%);
    }
`;

export default Label
