import React from 'react'
import styled from 'styled-components';

import useI18n from 'core/i18n/hooks/useI18n'

import {FORM_TITLE} from './locales'

const SubmitButton = styled.button`
    font: inherit;
    border-radius: 3px;
    padding: 0.5rem 1rem;
    width: 10rem;
    background: transparent;
    border: 2px solid black;
    cursor: pointer;

    &:hover {
        background: black;
        color: white;
    }
`;

const Form = styled.form`
    border: 1px solid black;
    padding: 1rem 2rem;
    width: 20vw;
`;

const FormControl = styled.div`
    display: flex;
    flex-direction: column;
    margin: 1rem 0;
`;

const Stepper = styled.ul`
    display: flex;
    width: 100%;
    border: 2px solid black;
    justify-content: space-between;
`;

const Step = styled.li`
    list-style: none;
`;

const RegisterForm = () => {
    const I18n = useI18n()

    return (
        <div>
            <h2>{I18n.t(FORM_TITLE)}</h2>
            <Stepper>
                <Step>Step 1</Step>
                <Step>Step 2</Step>
                <Step>Step 3</Step>
            </Stepper>
            <Form>
                <FormControl>
                    <label>First Name</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Last Name</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Nickname</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Interested Topic</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Allergic food</label>
                    <input type="text"/>
                </FormControl>
                <FormControl>
                    <label>Congenital Disease</label>
                    <input type="text"/>
                </FormControl>
                <SubmitButton>Register</SubmitButton>
            </Form>
        </div>
    )
}

export default RegisterForm
