import styled from "styled-components";

const RadioGroup = styled.div`
    display: flex;
    align-items: center;
    flex-direction: row;
    margin-top: 0.7rem;
    padding-left: 1rem;

    @media only screen and (max-width: 768px) {
        flex-direction: column;
    }
`;

export default RadioGroup
